    background-color: #181818;
  }
  /* style inline HTML graphql cant be generated */
  .graphiql-explorer-root input[style*="color: rgb(210, 5, 78)"] {
    border-color: currentcolor currentcolor #999 !important;
    background-color: #181818;
  }
  .graphiql-explorer-root input[style*="border-color: currentcolor currentcolor rgb(136, 136, 136)"] {
    border-color: currentcolor currentcolor #999 !important;
  }
  .graphiql-explorer-node span[style*="color: rgb(31, 97, 160)"] {
    color: #4183c4 !important;
  }
  .graphiql-explorer-node span[style="color: rgb(139, 43, 185)"] {
    color: #9a6cdf !important;
  }
  #graphiql svg path[d*="2V16H2V2H16ZM16"][fill="#CCC"] {
    fill: #444;
  }
  #graphiql svg path[d*="0H2C0.9"][fill="#666"] {
    fill: #777;
  }
  .graphiql-ide.signed-out::after {
    background: #333;
  }
  .graphiql-container .CodeMirror-foldmarker {
    background: /*[[base-color]]*/ !important;
  }
  .graphiql-container .execute-button svg {
    fill: currentColor !important;
  }
  /* Actions runs */
  .CheckStep div[style*="opacity:0.5"] {
    opacity: 1 !important;
  }
  .CheckRun-search-input:focus {
    background-color: hsla(0, 0%, 100%, .05);
  }
  /*[[code-wrap]]*/
  /* inline code - single lines */
  .markdown-body code, .markdown-body tt, .protip code, .content dl code,
  .content ul code, p code, .content .sectionbody .dlist dt, .content p > tt,
  .content .sectionbody .dlist dt, .content p > tt, .commit-sha,
  .commit-preview .message pre, .rendered_html pre, .rendered_html code,
  .markdown-format p > code, .markdown-format pre, .rgh-parse-backticks {
    background: #222 !important;
  }
  .project-card code {
    background: #333 !important;
  }
  /* Styles auto-complete popups in the actions workflow editor */
  .CodeMirror-hints {
    background: #181818;
    border-color: #404040;
    box-shadow: 0 1px 5px #000;
  }
  .CodeMirror-hint {
    color: #d2d2d2;
  }
  .CodeMirror-hint-active {
    color: #f0f0f0;
    background-color: /*[[base-color]]*/;
  }
  .CodeMirror-hint-active .CodeMirror-hint-description {
    color: #f0f0f0 !important;
  }
  /*[[syntax-theme]]*//*[[syntax-codemirror]]*//*[[syntax-jupyter]]*/
  /* inline code - big blocks */
  .markdown-format pre, .markdown-body pre, .highlight pre, .markdown-body pre,
  #facebox pre, .blob-expanded, .terminal, .copyable-terminal,
  #notebook .input_area, .blob-code-context, .markdown-format code, .api pre,
  .api li:not(a) code, .hook-delivery-details pre, .hook-delivery-container pre,
  .code-list .file-box, .CodeMirror, .highlight.bg-gray {
    background-color: var(--ghd-code-background) !important;
    color: var(--ghd-code-color) !important;
  }
  .flash p code {
    background-color: inherit !important;
  }
  .blob-code, .blob-code-inner {
    color: var(--ghd-code-color) !important;
  }
